mapper.select:
select no, c.content_type_id, content_id, title, content_type_name, sido_name, gugun_name, first_image1, first_image2, map_level, latitude, longitude, tel, a.addr1 addr, homepage, overview, a.view_cnt from attractions a join contenttypes c on (a.content_type_id = c.content_type_id) join (select s.sido_code sido, g.gugun_code gugun, s.sido_name sido_name, g.gugun_name gugun_name from sidos s join guguns g on (s.sido_code = g.sido_code)) sg on (a.area_code = sg.sido and a.si_gun_gu_code = sg.gugun) where 1=1
and title like CONCAT('%', #{keyword}, '%')
and c.content_type_name = #{contentTypeName}
and sido_name = #{areaCode}
and gugun_name = #{siGunGuCode}
order by title limit #{offset}, #{limit}

select count(*) as total from attractions a join contenttypes c on (a.content_type_id = c.content_type_id) join (select s.sido_code sido, g.gugun_code gugun, s.sido_name sido_name, g.gugun_name gugun_name from sidos s join guguns g on (s.sido_code = g.sido_code)) sg on (a.area_code = sg.sido and a.si_gun_gu_code = sg.gugun) where 1=1
and title like CONCAT('%', #{keyword}, '%')
and c.content_type_name = #{contentTypeName}
and sido_name = #{areaCode}
and gugun_name = #{siGunGuCode}

